"use client";

import { useEffect, useState } from "react";
import {
  Dialog,
  DialogContent,
} from "@/components/ui/dialog";
import { fetchMovieDetails, getImageUrl, Movie } from "@/lib/tmdb";
import { Star, Calendar, Users, Loader2 } from "lucide-react";
import Image from "next/image";

interface MovieDetailModalProps {
  movie: Movie | null;
  isOpen: boolean;
  onClose: () => void;
}

export function MovieDetailModal({ movie, isOpen, onClose }: MovieDetailModalProps) {
  const [details, setDetails] = useState<Record<string, any> | null>(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (movie && isOpen) {
      const loadData = async () => {
        setLoading(true);
        try {
          const data = await fetchMovieDetails(movie.id);
          setDetails(data);
        } catch (err) {
          console.error(err);
        } finally {
          setLoading(false);
        }
      };
      loadData();
    } else {
      setDetails(null);
    }
  }, [movie, isOpen]);

  if (!movie) return null;

  const trailer = details?.videos?.results?.find(
    (v: any) => v.type === "Trailer" && v.site === "YouTube"
  );

  return (
    <Dialog open={isOpen} onOpenChange={(open) => !open && onClose()}>
      <DialogContent className="max-w-5xl border-white/10 bg-zinc-950 p-0 text-white overflow-hidden">
        <div className="relative aspect-video w-full bg-black">
          {loading ? (
            <div className="flex h-full w-full items-center justify-center">
              <Loader2 className="h-10 w-10 animate-spin text-white/20" />
            </div>
          ) : trailer ? (
            <iframe
              src={`https://www.youtube.com/embed/${trailer.key}?autoplay=1`}
              className="h-full w-full"
              allow="autoplay; encrypted-media"
              allowFullScreen
            />
          ) : (
            <Image
              src={getImageUrl(movie.backdrop_path, "original")}
              alt={movie.title}
              fill
              className="object-cover opacity-50"
            />
          )}
        </div>

        <div className="p-8 md:p-12">
          <div className="grid grid-cols-1 gap-12 lg:grid-cols-3">
            <div className="lg:col-span-2 space-y-6">
              <div className="space-y-2">
                <h2 className="text-4xl font-black uppercase tracking-tighter md:text-6xl">
                  {movie.title}
                </h2>
                <div className="flex items-center gap-6 text-sm font-bold tracking-widest text-white/40 uppercase">
                  <div className="flex items-center gap-2">
                    <Calendar className="h-4 w-4" />
                    {movie.release_date.split("-")[0]}
                  </div>
                  <div className="flex items-center gap-2">
                    <Star className="h-4 w-4 fill-theme-primary text-theme-primary" />
                    {movie.vote_average.toFixed(1)}
                  </div>
                </div>
              </div>

              <p className="text-lg font-light leading-relaxed text-white/60">
                {movie.overview}
              </p>

              {details?.credits?.cast && (
                <div className="space-y-4">
                   <h3 className="flex items-center gap-2 text-xs font-black tracking-[0.3em] uppercase text-white/20">
                    <Users className="h-4 w-4" />
                    CAST
                  </h3>
                  <div className="flex flex-wrap gap-2">
                    {details.credits.cast.slice(0, 8).map((person: { id: number; name: string }) => (
                      <span 
                        key={person.id}
                        className="rounded-full border border-white/10 bg-white/5 px-4 py-1.5 text-xs font-bold tracking-tight text-white/60 transition-colors hover:border-white/30 hover:text-white"
                      >
                        {person.name}
                      </span>
                    ))}
                  </div>
                </div>
              )}
            </div>

            <div className="space-y-8">
              <div className="rounded-lg border border-white/5 bg-white/5 p-6 backdrop-blur-md">
                <button 
                  className="w-full rounded-full bg-white py-4 font-black tracking-tighter text-black transition-transform hover:scale-105 active:scale-95 uppercase"
                >
                  WATCH NOW
                </button>
                <p className="mt-4 text-center text-[10px] font-bold tracking-widest text-white/20 uppercase">
                  Available in Cinema &amp; Streaming
                </p>
              </div>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

const TMDB_API_KEY = process.env.NEXT_PUBLIC_TMDB_API_KEY;
const TMDB_BASE_URL = 'https://api.themoviedb.org/3';
const IMAGE_BASE_URL = 'https://image.tmdb.org/t/p';

export interface Movie {
  id: number;
  title: string;
  overview: string;
  poster_path: string;
  backdrop_path: string;
  release_date: string;
  vote_average: number;
}

export const getImageUrl = (path: string, size: 'w500' | 'original' = 'w500') => {
  if (!path) return '';
  return `${IMAGE_BASE_URL}/${size}${path}`;
};

export async function fetchTrendingMovies(): Promise<Movie[]> {
  const response = await fetch(
    `${TMDB_BASE_URL}/trending/movie/day?api_key=${TMDB_API_KEY}`
  );
  const data = await response.json();
  return data.results;
}

export async function fetchPopularMovies(): Promise<Movie[]> {
  const response = await fetch(
    `${TMDB_BASE_URL}/movie/popular?api_key=${TMDB_API_KEY}`
  );
  const data = await response.json();
  return data.results;
}

export async function fetchMovieDetails(movieId: number) {
  const response = await fetch(
    `${TMDB_BASE_URL}/movie/${movieId}?api_key=${TMDB_API_KEY}&append_to_response=videos,credits`
  );
  return response.json();
}

export async function fetchMovieTrailers(movieId: number) {
  const response = await fetch(
    `${TMDB_BASE_URL}/movie/${movieId}/videos?api_key=${TMDB_API_KEY}`
  );
  const data = await response.json();
  return data.results.filter((v: Record<string, any>) => v.type === "Trailer" && v.site === "YouTube");
}

export async function fetchMovieCredits(movieId: number) {
  const response = await fetch(
    `${TMDB_BASE_URL}/movie/${movieId}/credits?api_key=${TMDB_API_KEY}`
  );
  return response.json();
}
